{
    "query" : {
        "query_string" : {
            "query" : "formatVersion:1.0 AND -distributionVersion:*SNAPSHOT AND distributionId:\"org.xwiki.enterprise:xwiki-enterprise-web\""
        }
    }
}
')
#set ($result = $services.activeinstalls.searchInstalls("install", $query, {"search_type": "count"}))
