##=========================
## New way for XWiki 6.1M1+
##=========================
#set ($query = '
{
   "query": {
      "query_string": {
         "query": "-distributionVersion:*SNAPSHOT AND distributionId:\"org.xwiki.enterprise:xwiki-enterprise-web\""
      }
   },
   "aggs": {
      "instanceId_count": {
         "cardinality": {
            "field": "instanceId"
         }
      }
   }
}
')
#set ($result = $services.activeinstalls.searchInstalls("install2", $query, {"search_type": "count"}))
## If there's an error and the "xpage=plain" URL parameter is not passed (ie it's not called by the Counter widget) then display the error if any. Otherwise display the invalid numeric result.
#if (!$result)
  #set ($errorText = $!services.activeinstalls.lastError)
  #if ("$!request.xpage" != 'plain')
    {{error}}{{{$errorText}}}{{/error}}
  #else
    #set ($count = 0)
  #end
#else
  #set ($count = $mathtool.add($count, $result.getAsJsonObject("aggregations").getAsJsonObject("instanceId_count").getAsJsonPrimitive("value").getAsLong()))
  $count
#end
